import React from 'react';
import './App.css';

import { connect } from 'react-redux';

import LoginPage from '../LoginPage/LoginPage';
import MainPage from '../MainPage/MainPage';

class App extends React.Component {
  constructor ( props ) {
    super ( props ); 
    this.state = {
      curPage : <LoginPage/>
    }
  }
  retApp = () => {
    let pp = <LoginPage/>;
    if ( this.props.curPage == "LOGIN") {
      this.setState((state) => {
        return {curPage: <LoginPage/>};
      })
    }
    else {
      this.setState((state) => {
        return {curPage: <MainPage/>};
      });
    }    
  }
  componentDidUpdate = () => {
    this.retApp();
  }
  render() {
    return (
      <div className="App">
        {this.state.curPage}
      </div>
    )
  }
}

let mapStateToProps = ( store ) => {
  return {
    curPage : store.curPage
  }
}

export default connect(mapStateToProps) (App);
